package Session2;

public class SolvingExpression {


	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=5, y=10, value1, value2, value3, value4;
		value1 = (x + y) * 2;
		value2 = x-y+2;
		value3 = (x+y)*2;
		value4 = x%y;
		System.out.println("x=5, y=10 (x+y)*2= "+ value1);
		System.out.println("x=5, y=10 (x-y)+2= "+ value2);		
		System.out.println("x=5, y=10 (x+y)*2= "+ value3);		
		System.out.println("x=5, y=10 x%y= "+ value4);		
	}
}
